mapper.delete:

delete
From tb_cntnt_bkmk
where cntnt_bkmk_mber_id = #{mberId}
  and cntnt_bkmk_cntnt_id = #{cntntId}

mapper.select:

SELECT count(cntnt_bkmk_mber_id)
FROM tb_cntnt_bkmk
WHERE cntnt_bkmk_mber_id = #{mberId}
  AND cntnt_bkmk_cntnt_id = #{cntntId}

mapper.delete:

delete
From tb_goods_bkmk
where goods_bkmk_mber_id = #{mberId}
  and goods_bkmk_goods_id = #{goodsId}
