mapper.select:
SELECT cntnt_id, cntnt_cate_code, cntnt_title, cntnt_kwrd, cntnt_url, cntnt_regist_date, cntnt_update_date, cntnt_delete_yn FROM (SELECT rownum rn, cntnt_id, cntnt_cate_code, cntnt_title, cntnt_kwrd, cntnt_url, cntnt_regist_date, cntnt_update_date, cntnt_delete_yn FROM (SELECT * FROM tb_cntnt a JOIN tb_common_code b ON a.cntnt_cate_code = b.common_code WHERE cntnt_delete_yn = 'N'
AND common_code_val = #{commonCodeVal}
ORDER BY cntnt_id desc ) ) WHERE rn between #{start} AND #{end} ORDER BY rn
