mapper.select:
select commu_id, commu_title, commu_regist_date, commu_read_cnt, commu_cate_code, commu_mber_id, rn, common_code_val from ( select rownum rn, commu_id, commu_title, commu_regist_date, commu_read_cnt, commu_cate_code, commu_mber_id, commu_status, common_code_val from ( select * from tb_commu a join tb_common_code b on a.commu_cate_code = b.common_code where commu_status = 'N'
and b.common_code_val = #{commonCodeVal}
order by commu_id desc ) ) WHERE rn BETWEEN #{start} AND #{end} AND commu_status != 'Y' AND commu_status != 'R' ORDER BY commu_id desc
