mapper.select:
SELECT a.commu_id, a.commu_title, a.commu_regist_date, a.commu_read_cnt, a.commu_cate_code, a.commu_mber_id, b.common_code_val FROM tb_commu a JOIN tb_common_code b on a.commu_cate_code = b.common_code WHERE a.commu_status != 'Y'
AND b.common_code_val = #{commuCateCode}
ORDER BY commu_id DESC

mapper.update:

update tb_commu
set commu_read_cnt = commu_read_cnt + 1
where commu_id = #{commuId}


update tb_commu
set commu_status='R'
where commu_id=#{commuId}
